<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Hotel Paradise</title>
    <!-- Bootstrap CSS -->
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
    <style>
        body {
            font-family: Arial, sans-serif;
        }
        header {
            margin-bottom: 20px;
        }
        .card {
            border: 1px solid #ddd;
            border-radius: 5px;
        }
        footer {
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <header class="bg-primary text-white text-center py-3">
        <h1>Hotel Paradise</h1>
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
            <a class="navbar-brand" href="#">Hotel Paradise</a>
            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
                <ul class="navbar-nav">
                    <li class="nav-item">
                        <a class="nav-link" href="#about">About</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="#rooms">Rooms</a>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="#booking">Book Now</a>
                    </li>
                </ul>
            </div>
        </nav>
    </header>

    <section id="about" class="container my-5">
        <h2 class="text-center mb-4">About Us</h2>
        <p>Welcome to Hotel Paradise, your perfect getaway. Enjoy luxury and comfort at our beautifully designed hotel.</p>
    </section>

    <section id="rooms" class="container my-5">
        <h2 class="text-center mb-4">Our Rooms</h2>
        <div class="row">
            <div class="col-md-6 mb-4">
                <div class="card">
                    <img src="data:image/jpeg;base64," class="card-img-top" alt="Deluxe Room">
                    <div class="card-body">
                        <h5 class="card-title">Deluxe Room</h5>
                        <p class="card-text">Spacious and luxurious with stunning views.</p>
                    </div>
                </div>
            </div>
            <div class="col-md-6 mb-4">
                <div class="card">
                    <img src="data:image/jpeg;base64," class="card-img-top" alt="Standard Room">
                    <div class="card-body">
                        <h5 class="card-title">Standard Room</h5>
                        <p class="card-text">Comfortable and cozy for a relaxing stay.</p>
                    </div>
                </div>
            </div>
        </div>
    </section>

    <section id="booking" class="container my-5">
        <h2 class="text-center mb-4">Book Now</h2>
        <form id="bookingForm">
            <div class="form-row">
                <div class="form-group col-md-6">
                    <label for="name">Name:</label>
                    <input type="text" class="form-control" id="name" name="name" required>
                </div>
                <div class="form-group col-md-6">
                    <label for="email">Email:</label>
                    <input type="email" class="form-control" id="email" name="email" required>
                </div>
            </div>
            <div class="form-row">
                <div class="form-group col-md-6">
                    <label for="checkin">Check-in Date:</label>
                    <input type="date" class="form-control" id="checkin" name="checkin" required>
                </div>
                <div class="form-group col-md-6">
                    <label for="checkout">Check-out Date:</label>
                    <input type="date" class="form-control" id="checkout" name="checkout" required>
                </div>
            </div>
            <button type="submit" class="btn btn-primary">Submit Booking</button>
        </form>
        <div id="confirmationMessage" class="mt-4"></div>
    </section>

    <footer class="bg-dark text-white text-center py-3">
        <p>&copy; 2024 Hotel Paradise. All rights reserved.</p>
    </footer>

    <!-- Bootstrap JS, Popper.js, and jQuery -->
    <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.6/dist/umd/popper.min.js"></script>
    <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const form = document.getElementById('bookingForm');
            const confirmationMessage = document.getElementById('confirmationMessage');

            form.addEventListener('submit', (event) => {
                event.preventDefault();

                // Collect form data
                const name = document.getElementById('name').value;
                const email = document.getElementById('email').value;
                const checkin = document.getElementById('checkin').value;
                const checkout = document.getElementById('checkout').value;

                // Display a confirmation message
                confirmationMessage.innerHTML = `
                    <div class="alert alert-success">
                        <h4 class="alert-heading">Thank you for your booking, ${name}!</h4>
                        <p>We have received your booking details:</p>
                        <ul>
                            <li>Email: ${email}</li>
                            <li>Check-in Date: ${checkin}</li>
                            <li>Check-out Date: ${checkout}</li>
                        </ul>
                    </div>
                `;
                
                // Clear the form
                form.reset();
            });
        });
    </script>
</body>
</html>








